  #{msName,jdbcType=VARCHAR}, #{msIntro,jdbcType=VARCHAR}, #{showFlag,jdbcType=VARCHAR}
  )

mapper.select:
select id, ml_id, ms_id, ms_name, ms_intro, show_flag
from nl_model_small
where id = #{id,jdbcType=INTEGER}

select
    id, ml_id, ms_id, ms_name, ms_intro, show_flag
from nl_model_small where ml_id=#{mlId}

select
    id, ml_id, ms_id, ms_name, ms_intro, show_flag
from nl_model_small where ms_name=#{msName}
