mapper.update:
update nl_cminfo
set cm_id = #{cmId,jdbcType=VARCHAR},
  cpu_rate = #{cpuRate,jdbcType=VARCHAR},
  mem_rate = #{memRate,jdbcType=VARCHAR},
  time = #{time,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, cm_id, cpu_rate, mem_rate, time
from nl_cminfo
where id = #{id,jdbcType=INTEGER}
